angular.module("app",["jsonFormatter"]).controller("controller",["$scope",function(t){t.slideMap={2:"image_likert"},t.translate=function(n){return n.map(function(n){var e={};return n.buttons&&n.buttons.length&&(e.options=n.buttons.map(function(t){return{weight:0,text:t.text,correct:1==n.slide_type_id||4==n.slide_type_id||2==n.slide_type_id}})),e.template=t.slideMap[n.slide_type_id],e.content=n.contents.description?$(n.contents.description).text():n.contents.question?$(n.contents.question).text():n.contents.info?$(n.contents.info).text():null,n.image&&!n.image.includes("missing")&&(e.image_url="https://qa.wellopp.com"+n.image,e.image_align=Math.random()>.5?"Left":"Right"),n.audio&&!n.audio.includes("missing")&&(e.audio=n.audio),e})},t.strip=function(t){return t.map(function(t){return t.form="",t})},t.input={},t.output={};t.init=function(){$.getJSON("https://sb.wellopp.com/api/v1/surveys/264/slides.json",function(n){console.log(n),t.$apply(function(){t.input=t.strip(n),t.output=t.translate(n)})})},t.init()}]);